import { defineStore } from 'pinia';

export const useAppState = defineStore('appState', {
    state: () => {
        return {
            _romIsLoaded: false,
            view: 'Home',
            viewName: 'WPC Edit'
        }
    },
    getters: {
        currentView: (state) => state.view,
        title: function() {
            console.log("name: "+this.viewName);
            return this.viewName;
        },
        romIsLoaded: (state) => state._romIsLoaded,
        isAtHomeView: (state) => {
            if(state.view == 'Home') {
                return true;
            } else {
                return false;
            }
        },
    },
    actions: {
        setView(view) {
            this.view = view;
        },
        setViewTitle(viewName) {
            this.viewName = viewName;
        },
        setRomStatus(isLoaded) {
            this._romIsLoaded = isLoaded;
        },
    }
})
