onmessage = function(event) {
    const data = event.data
    const DOT_SIZE = 2;
    const index = data.index;
    const canvas = data.canvas;
    const ctx = canvas.getContext('2d');
    const plane = data.plane;
    for(let i=0; i<128*32; i++) {
        const dotX = (i % 128) * DOT_SIZE;
        const dotY = Math.floor(i / 128) * DOT_SIZE;

        const width = 128;
        const col = i % width;
        const byte = Math.floor(i/8);
        const bitPos = (col % 8);
        const dotVal =  ( plane[byte] & 0xFF ) >> (bitPos) & 1;

        ctx.beginPath();
        ctx.beginPath();
        ctx.rect(dotX, dotY, DOT_SIZE, DOT_SIZE);
        ctx.fillStyle = `rgba(250, 136, 2, ${dotVal*255})`;
        ctx.fill();
    }
    const bitmap = canvas.transferToImageBitmap();
    self.postMessage({msg: 'render', bitmap, index});
  }
