export default {
  name: 'NumeralInput',
  props: {
    defaultVal: Number,
    max: Number,
    min: Number,
    maxlength: Number,
    size: String
  },
  methods: {
    getClass() {
      let className = "small";
      if (this.size == "large") {
        className = "large";
      }
      return className;
    },
    limitLength() {
      const input = this.$refs.input;
      const value = input.value;
      console.log(value.length);
      if (value.length > this.maxlength - 1) {
        input.value = value.slice(0, this.maxlength);
        return false;
      }
    }
  }
};
