import { defineStore } from 'pinia';

export const useAppState = defineStore('appState', {
    state: () => {
        return {
            romIsLoaded: false,
            viewTitle: 'WPC Edit'
        }
    },/*
    getters: {
        currentView: (state) => state.view,
        title: () => this.viewName,
        romIsLoaded: (state) => state._romIsLoaded,
        isAtHomeView: (state) => {
            if(state.view == 'Home') {
                return true;
            } else {
                return false;
            }
        },
    },*/
    actions: {
        setViewTitle(viewTitle) {
            this.viewTitle = viewTitle;
            console.log("name: "+this.viewTitle);
        },
        setRomStatus(isLoaded) {
            this.romIsLoaded = isLoaded;
        },
    }
})
