import { defineStore } from 'pinia';
import { WPCEdit } from 'wpcedit';

export const useWpcEditStore = defineStore('wpcEditStore', {
    state: () => {
        return {
            romIsLoaded: false,
            viewTitle: 'WPC Edit',
            isAtHomeView: true
        }
    },
    getters: {
        maxFullFrameIndex: (state) => {
            console.log(state);
            return 20; //TEMP! Get value from ROM!
        },
    },
    actions: {
        loadRom(data) {
            WPCEdit.init();
            WPCEdit.rom = data;
            this.romIsLoaded = true;
            WPCEdit.fullFrameImage.index = 1;
            console.log(WPCEdit.fullFrameImage.fullFrameImage.plane)
        },
        getFullFrameImageAt(index) {
            WPCEdit.fullFrameImage.index = index;
            return WPCEdit.fullFrameImage.plane;
        }
    }
})
