import { defineStore } from 'pinia';

export const useAppState = defineStore('appState', {
    state: () => {
        return {
            _romIsLoaded: false,
            _view: 'Home',
            _viewName: 'WPC Edit'
        }
    },
    getters: {
        currentView: (state) => state._view,
        viewName: (state) => state._viewName,
        romIsLoaded: (state) => state._romIsLoaded,
        isHome: (state) => {
            if(state._view == 'Home') {
                return true;
            } else {
                return false;
            }
        },
    },
    actions: {
        setView(view) {
            this._view = view;
        },
        setViewName(viewName) {
            this._viewName = viewName;
        },
        setRomStatus(isLoaded) {
            this._romIsLoaded = isLoaded;
        },
    }
})
