export default {
  name: 'StandardButton',
  props: {
    size: String,
    type: String
  },
  methods: {
    getClass() {
      let className = "small";
      if (this.size == "large") {
        className = "large";
      }
      if (this.type == "primary") {
        className = className + " primary";
      }
      return className;
    }
  }
};
