import { defineStore } from 'pinia';
import { WPCEdit } from 'wpcedit';

export const useWpcEditStore = defineStore('wpcEditStore', {
    state: () => {
        return {
            romIsLoaded: false,
            viewTitle: 'WPC Edit',
            isAtHomeView: true,
            WPCEdit: WPCEdit.init()
        }
    },
    getters: {
        maxFullFrameIndex: (state) => {
            console.log(state);
            return 20; //TEMP! Get value from ROM!
        },
    },
    actions: {
        loadRom(data) {
            WPCEdit.rom = data;
            this.romIsLoaded = true;

            console.log(WPCEdit.fullFrameImage.getFullFrameImageAt(1))
        },
        getFullFrameImageAt(index) {
            WPCEdit.init();
            return WPCEdit.fullFrameImage.getFullFrameImageAt(index);
        }
    }
})
