import { defineStore } from 'pinia';

export const useAppState = defineStore('appState', {
    state: () => {
        return {
            romIsLoaded: false,
            view: 'Home',
            viewName: 'WPC Edit'
        }
    },
    getters: {
        currentView: (state) => state.view,
        title: (state) => state.viewName,
        romStatus: (state) => state.romIsLoaded,
        isAtHomeView: (state) => {
            if(state.view == 'Home') {
                return true;
            } else {
                return false;
            }
        },
    },
    actions: {
        setView(view) {
            this.view = view;
        },
        setViewName(viewName) {
            this.viewName = viewName;
        },
        setRomStatus(isLoaded) {
            this.romIsLoaded = isLoaded;
        },
    }
})
