import { defineStore } from 'pinia';
import { WPCEdit } from 'wpcedit';
import { useStorage } from '@vueuse/core';
import { set, get } from "./idb.js";

export const useWpcEditStore = defineStore('wpcEditStore', {
    state: () => {
        return {
            romIsLoaded: useStorage('romIsLoaded',false),
            romIsAltered: useStorage('romIsAltered',false),
            viewTitle: 'WPC Edit',
            fileName: useStorage('fileName',''),
            isAtHomeView: true,
            currSpriteIndex: 0,
            currSpriteTableIndex: 0,
            currentRomData: null,
            spriteTableMap: null,
            calculatedChecksum: '',
            storedChecksum: '',
            versionByte: '',
            fixupBytes: '',
            checksumIsValid: false,
            exportOption: 0
        }
    },
    getters: {
        maxFullFrameIndex: (state) => {
            /*
            let index = 900;//WPCEdit.fullFrameImage.maxImageIndex;
            let invalidImages = 0;
            while(invalidImages<4 && index<2000) {
                const plane = WPCEdit.fullFrameImage.getPlaneAt(index);
                if(plane.type > 11 && plane.type != 255) {
                    invalidImages++;
                    console.log("INVALID at index: "+index);
                }
                index++;
            }

            //console.log("type: "+plane.type);

            console.log("max: "+index)
            return index;*/
            console.log(state);
            return 1000;
        }
    },
    actions: {
        getRomFromCache() {
            const cachedFileName = this.fileName;
            get('currentRomData').then(
                (value) => {
                    if (value != null) {
                        this.loadRom(value,cachedFileName);
                    }
                }
            );
        },
        loadRom(data,name) {
            WPCEdit.init();
            if(WPCEdit.setRom(data)) {
                this.fileName = name;
                this.romIsLoaded = true;
                this.currentRomData = data;
                set('currentRomData',this.currentRomData);
                this.spriteTableMap = WPCEdit.variableSizedImage.indexMap;
                this.storedChecksum = pad(WPCEdit.checksum.stored.toString(16),4).toUpperCase();
                this.calculatedChecksum = pad(WPCEdit.checksum.calculated.toString(16),4).toUpperCase();
                this.fixupBytes = pad(WPCEdit.checksum.delta.toString(16),4).toUpperCase();
                this.versionByte = pad((WPCEdit.checksum.stored & 0xFF).toString(16),2).toString(16);
                this.checksumIsValid = WPCEdit.checksum.isValid;
                //WPCEdit.verbose = true;
            }
            function pad(input, length) {
                return (Array(length + 1).join('0') + input).slice(-length);
            }

        },
        unloadRom() {
            set('currentRomData',null);
            this.currentRomData = null;
            this.romIsLoaded = false;
            this.romIsAltered = false;
            this.spriteTableMap = null;
            this.currSpriteIndex = 0;
            this.currSpriteTableIndex = 0;
            this.currentRomData = null;
            this.spriteTableMap = null;
            this.calculatedChecksum = 0;
            this.storedChecksum = 0;
            this.versionByte = 0;
            this.fixupBytes = 0;
            this.checksumIsValid = false;
        },
        getFullFrameImageAt(index) {
            return WPCEdit.fullFrameImage.getPlaneAt(index);
        },
        getSpriteAt(table,index) {
            WPCEdit.variableSizedImage.table = table;
            WPCEdit.variableSizedImage.index = index;
            return WPCEdit.variableSizedImage.plane;
        },
        getSpriteCentered(table,index) {
            WPCEdit.variableSizedImage.table = table;
            WPCEdit.variableSizedImage.index = index;
            const spritePlane = WPCEdit.variableSizedImage.plane
            const xOffset = Math.floor((128-spritePlane.width)/2);
            const yOffset = Math.floor((32-spritePlane.height)/2);
            const sprite = WPCEdit.variableSizedImage.placeInFullFrame(WPCEdit.variableSizedImage.plane,xOffset,yOffset)
            return sprite;
        },
        getSpriteAsFullFrame(sprite,posX,posY) {
            return sprite = WPCEdit.variableSizedImage.placeInFullFrame(sprite,posX,posY)
        },
        mergeImages(img1,img2,mask) {
            return WPCEdit.fullFrameImage.mergeImages(img1,img2,mask);
        },
        updateChecksum(version) {
            console.log("version is "+ version)
            const result = WPCEdit.checksum.update(version);
            if(result.status == 1) {
                this.loadRom(result.data,this.fileName);
                this.romIsAltered = true;
            }
            return result.status;
        },
        disableChecksum() {
            const newRom = WPCEdit.checksum.disable();
            this.loadRom(newRom,this.fileName);
            this.romIsAltered = true;
            const result = 1; //Temp! Use a status variable
            return result;
        },
        setExportOption(option) {
            this.exportOption = option;
        },
        exportAsDataArray(plane) {
            let dataString = '{ ';
            dataString += (Array.from(plane)).join(', ');
            dataString += ' }';
            return dataString;
        },
        exportAs1bitBmp(plane) {
            const image = drawArray(Array.from(plane),1);
            return image;
            function drawArray(arr, depth) {
                // Borrowed from https://gist.github.com/vukicevic/8112515
                var offset, data;

                function conv(size) {
                    return String.fromCharCode(size&0xff, (size>>8)&0xff, (size>>16)&0xff, (size>>24)&0xff);
                }

                offset = depth <= 8 ? 54 + Math.pow(2, depth)*4 : 54;

                //BMP Header
                data  = 'BM';                          // ID field
                data += conv(offset + arr.length);     // BMP size
                data += conv(0);                       // unused
                data += conv(offset);                  // pixel data offset

                //DIB Header
                data += conv(40);                       // DIB header length
                data += conv(128);                      // image height
                data += conv(32);                       // image width
                data += String.fromCharCode(1, 0);      // colour panes
                data += String.fromCharCode(depth, 0);  // bits per pixel
                data += conv(0);                        // compression method
                data += conv(arr.length);               // size of the raw data
                data += conv(2835);                     // horizontal print resolution
                data += conv(2835);                     // vertical print resolution
                data += conv(0);                        // colour palette, 0 == 2^n
                data += conv(0);                        // important colours

                //Grayscale tables for bit depths <= 8
                if (depth <= 8) {
                    data += conv(0);

                    for (var s = Math.floor(255/(Math.pow(2, depth)-1)), i = s; i < 256; i += s)  {
                        data += conv(i + i*256 + i*65536);
                    }
                }

                //Pixel data
                data += String.fromCharCode.apply(String, arr);

                return window.btoa(data);
            }

        },
        downloadRom() {
            const blob = new Blob([this.currentRomData], {
              type: 'application/octet-stream'
            });
            const url = window.URL.createObjectURL(blob);

            const nameWithoutExtension = this.fileName.replace(/\.[^/.]+$/, "");
            const extension = "rom";
            const date = new Date();
            const suffix = "_"+date.getFullYear()+""+pad(date.getMonth()+1,2)+""+pad(date.getDate(),2)+""+pad(date.getHours(),2)+""+pad(date.getMinutes(),2)+"_cs"+this.storedChecksum;
            const fileName = nameWithoutExtension + suffix + "." + extension;

            downloadURL(url, fileName);
            setTimeout(function() {
              return window.URL.revokeObjectURL(url);
            }, 1000);
            // Hacky, but standard, way to download blobs:
            // Turn it into a hidden href, then click the href and remove it
            function downloadURL(data, fileName) {
                const a = document.createElement('a');
                a.href = data;
                a.download = fileName;
                document.body.appendChild(a);
                a.style = 'display: none';
                a.click();
                a.remove();
              }
            function pad(input, length) {
                return (Array(length + 1).join('0') + input).slice(-length);
            }
        }
    },
    persist: {
        storage: localStorage
    },
})
