import { defineStore } from 'pinia';
import { WPCEdit } from 'wpcedit';

export const useWpcEditStore = defineStore('wpcEditStore', {
    state: () => {
        return {
            romIsLoaded: false,
            viewTitle: 'WPC Edit',
            isAtHomeView: true,
            currSpriteIndex: 0,
            currSpriteTableIndex: 0
        }
    },
    getters: {
        maxFullFrameIndex: (state) => {
            console.log(state);
            return 1000; //TEMP! Get value from ROM!
        },
        maxSpriteTable: () => {
            return 64; //TEMP! Get value from ROM!
        },
        maxSpriteIndexInCurrentTable: () => {
            return 1208; //TEMP! Get value from ROM!
        }
    },
    actions: {
        loadRom(data) {
            WPCEdit.init();
            WPCEdit.rom = data;
            this.romIsLoaded = true;
        },
        getFullFrameImageAt(index) {
            WPCEdit.fullFrameImage.index = index;
            return WPCEdit.fullFrameImage.plane;
        },
        getSpriteAt(table,index) {
            WPCEdit.variableSizedImage.next(1);
            console.log(table+ " "+index);

            /* implement a pattern like this in WPCEdit.js
            /* ===========================================
            const indexDelta = index - this.currSpriteIndex;
            if(indexDelta < 0) {
                WPCEdit.variableSizedImage.prev(Math.abs(indexDelta))
            } else {
                WPCEdit.variableSizedImage.next(indexDelta);
            }
            this.currSpriteIndex = index;
            */

            return WPCEdit.variableSizedImage.plane;
        }
    }
})
