import { defineStore } from 'pinia';
import { WPCEdit } from 'wpcedit';
import { useLocalStorage } from '@vueuse/core';

export const useWpcEditStore = defineStore('wpcEditStore', {
    state: () => {
        return {
            romIsLoaded: useLocalStorage('romIsLoaded',false),
            romIsAltered: useLocalStorage('romIsAltered',false),
            viewTitle: 'WPC Edit',
            fileName: useLocalStorage('fileName',''),
            isAtHomeView: useLocalStorage('isAtHomeView',true),
            currSpriteIndex: useLocalStorage('currSpriteIndex',0),
            currSpriteTableIndex: useLocalStorage('currSpriteTableIndex',0),
            currentRomData: useLocalStorage('currentRomData',new Uint8Array()),
            spriteTableMap: useLocalStorage('spriteTableMap',new Array),
            calculatedChecksum: useLocalStorage('calculatedChecksum',0),
            storedChecksum: useLocalStorage('storedChecksum',0),
            versionByte: useLocalStorage('versionByte',0),
            fixupBytes: useLocalStorage('fixupBytes',0),
            checksumIsValid: useLocalStorage('checksumIsValid',false)
        }
    },
    getters: {
        maxFullFrameIndex: (state) => {
            /*
            let index = 900;//WPCEdit.fullFrameImage.maxImageIndex;
            let invalidImages = 0;
            while(invalidImages<4 && index<2000) {
                const plane = WPCEdit.fullFrameImage.getPlaneAt(index);
                if(plane.type > 11 && plane.type != 255) {
                    invalidImages++;
                    console.log("INVALID at index: "+index);
                }
                index++;
            }

            //console.log("type: "+plane.type);

            console.log("max: "+index)
            return index;*/
            console.log(state);
            return 1000;
        }
    },
    actions: {
        loadRom(data,name) {
            WPCEdit.init();
            if(WPCEdit.setRom(data)) {
                this.fileName = name;
                this.romIsLoaded = true;
                this.currentRomData = data;
                this.spriteTableMap = WPCEdit.variableSizedImage.indexMap;
                this.storedChecksum = pad(WPCEdit.checksum.stored.toString(16),4).toUpperCase();
                this.calculatedChecksum = pad(WPCEdit.checksum.calculated.toString(16),4).toUpperCase();
                this.fixupBytes = pad(WPCEdit.checksum.delta.toString(16),4).toUpperCase();
                this.versionByte = pad((WPCEdit.checksum.stored & 0xFF).toString(16),2).toString(16);
                this.checksumIsValid = WPCEdit.checksum.isValid;
                //WPCEdit.verbose = true;
            }
            function pad(input, length) {
                return (Array(length + 1).join('0') + input).slice(-length);
            }

        },
        unloadRom() {
            this.romIsLoaded = false;
            this.romIsAltered = false;
            this.currentRomData = null;
            this.spriteTableMap = null;
            this.currSpriteIndex = 0;
            this.currSpriteTableIndex = 0;
            this.currentRomData = null;
            this.spriteTableMap = null;
            this.calculatedChecksum = 0;
            this.storedChecksum = 0;
            this.versionByte = 0;
            this.fixupBytes = 0;
            this.checksumIsValid = false;
        },
        getFullFrameImageAt(index) {
            return WPCEdit.fullFrameImage.getPlaneAt(index);
        },
        getSpriteAt(table,index) {
            WPCEdit.variableSizedImage.table = table;
            WPCEdit.variableSizedImage.index = index;
            return WPCEdit.variableSizedImage.plane;
        },
        getSpriteCentered(table,index) {
            WPCEdit.variableSizedImage.table = table;
            WPCEdit.variableSizedImage.index = index;
            const spritePlane = WPCEdit.variableSizedImage.plane
            const xOffset = Math.floor((128-spritePlane.width)/2);
            const yOffset = Math.floor((32-spritePlane.height)/2);
            const sprite = WPCEdit.variableSizedImage.placeInFullFrame(WPCEdit.variableSizedImage.plane,xOffset,yOffset)
            return sprite;
        },
        getSpriteAsFullFrame(sprite,posX,posY) {
            return sprite = WPCEdit.variableSizedImage.placeInFullFrame(sprite,posX,posY)
        },
        mergeImages(img1,img2,mask) {
            return WPCEdit.fullFrameImage.mergeImages(img1,img2,mask);
        },
        updateChecksum(version) {
            console.log("version is "+ version)
            const result = WPCEdit.checksum.update(version);
            if(result.status == 1) {
                this.loadRom(result.data,this.fileName);
                this.romIsAltered = true;
            }
            return result.status;
        },
        disableChecksum() {
            const newRom = WPCEdit.checksum.disable();
            this.loadRom(newRom,this.fileName);
            this.romIsAltered = true;
            const result = 1; //Temp! Use a status variable
            return result;
        },
        downloadRom() {
            const blob = new Blob([this.currentRomData], {
              type: 'application/octet-stream'
            });
            const url = window.URL.createObjectURL(blob);

            const nameWithoutExtension = this.fileName.replace(/\.[^/.]+$/, "");
