import "core-js/modules/es.array.push.js";
import TopBar from './components/ui/TopBar.vue';
import store from "./stores/store.js";
import { storeToRefs } from 'pinia';
import { useMainStore } from './stores/mainStore.js';
const MainStore = useMainStore(store);
export default {
  name: 'App',
  components: {
    TopBar
  },
  props: {},
  setup() {
    const {
      viewTitle,
      isAtHomeView,
      romIsLoaded,
      currentRomData
    } = storeToRefs(MainStore);
    return {
      viewTitle,
      isAtHomeView,
      romIsLoaded,
      currentRomData
    };
  },
  data() {
    return {
      showFullFrameModal: false,
      showSpriteModal: false,
      modalProps: {
        index: 0,
        table: 0
      }
    };
  },
  methods: {
    openModal(value) {
      if (value.type == "full") {
        this.showFullFrameModal = true;
        this.modalProps.index = value.index;
      }
      if (value.type == "sprite") {
        this.showSpriteModal = true;
        this.modalProps.index = value.index;
        this.modalProps.table = value.table;
      }
    }
  },
  mounted() {
    // restore rom data from cache:
    console.log('this.romIsLoaded ' + this.romIsLoaded);
    console.log('this.currentRomData ' + this.currentRomData);
    if (this.romIsLoaded && this.currentRomData == null) {
      this.$router.push('/'); // force back to main psge, because the data has not been loaded yet
      MainStore.getRomFromCache();
    }
  }
};
